INSERT INTO `Poison` VALUES ('1','Stormclaw Scorpion Venom','5','250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nStormclaw Scorpion Venom\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=1\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Stormclaw Scorpion Venom<br/><span class=\"level\">Level 5 Poison</span></h1><p class=\"flavor\"><i>This purple-black venom attacks the nerves.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;250 gp<br/><b>Attack:</b> +5 vs. Fortitude; ongoing 5 poison damage and immobilized (save ends both).<br/><b><i>Aftereffect:</i></b> The target is immobilized (save ends).<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('2','Deathjump Spider Venom','5','250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDeathjump Spider Venom\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=2\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Deathjump Spider Venom<br/><span class=\"level\">Level 5 Poison</span></h1><p class=\"flavor\"><i>Looking like nothing so much as black mud, this poison is a favorite weapon of lizardfolk darters.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;250 gp<br/><b>Attack:</b> +8 vs. Fortitude; ongoing 5 poison damage and slowed (save ends both).<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('3','Carrion Crawler Brain Juice','5','250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCarrion Crawler Brain Juice\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=3\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Carrion Crawler Brain Juice<br/><span class=\"level\">Level 5 Poison</span></h1><p class=\"flavor\"><i>This venom is a clear, green liquid with a vile odor.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;250 gp<br/><b>Attack:</b> +8 vs. Fortitude; ongoing 5 poison damage and slowed (save ends both).<br/><b><i>First Failed Saving Throw:</i></b> The target is immobilized instead of slowed (save ends).<br/><b><i>Second Failed Saving Throw:</i></b> The target is stunned instead of immobilized (save ends).<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('4','Fiend\'s Breath','10','1250','Vor Rukoth','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFiend\'s Breath\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=4\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Fiend\'s Breath<br/><span class=\"level\">Level 10 Poison</span></h1><p class=\"flavor\"><i>Created from the powdered bones of devils and murdered humanoids, this poison is an intoxicant in limited quantitiesâ€”in larger doses, it means death.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1250 gp<br/><b>Attack:</b> +13 vs. Will; the target is weakened (save ends).<br/><b><i>First Failed Saving Throw:</i></b> The target is also dominated (save ends both). It uses its one action to attack the nearest creature during its turn (determined randomly for multiple creatures).<br/><b><i>Second Failed Saving Throw:</i></b> The target also takes ongoing 10 fire damage (save ends all).<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/197400000\" target=\"_new\">Vor Rukoth: An Ancient Ruins Adventure Site</a>, page(s) 14.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('5','Ground Thassil Root','5','250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGround Thassil Root\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=5\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Ground Thassil Root<br/><span class=\"level\">Level 5 Poison</span></h1><p class=\"flavor\"><i>This poison is a flavorless, blue powder.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;250 gp<br/><b>Attack:</b> +8 vs. Fortitude; slowed (save ends).<br/><b><i>First Failed Saving Throw:</i></b> The target is immobilized instead of slowed (save ends).<br/><b><i>Second Failed Saving Throw:</i></b> The target is unconscious for 1d4 hours.<br/><b>Special:</b> Thassil root can be delivered only by way of food or drink. It makes its first attack 2d6 minutes after its victim consumes it. All saving throws against this poison are made with a -2 penalty.<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('6','Dark Toxin','10','1250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDark Toxin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=6\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Dark Toxin<br/><span class=\"level\">Level 10 Poison</span></h1><p class=\"flavor\"><i>This poison, used by dark stalkers, looks like liquid shadow and smells like mushrooms.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1250 gp<br/><b>Attack:</b> +13 vs. Fortitude; ongoing 5 poison damage (save ends).<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Poison` VALUES ('7','Drow Poison','10','1250','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDrow Poison\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=poison&amp;id=7\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"poison\">Drow Poison<br/><span class=\"level\">Level 10 Poison</span></h1><p class=\"flavor\"><i>Distilled from demonic ichor in the temples of Lolth, this poison is the drow raceâ€™s favorite means of acquiring new slaves.</i></p><p><b>Poison</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1250 gp<br/><b>Attack:</b> +13 vs. Fortitude; the target takes a -2 penalty to attack rolls (save ends).<br/><b><i>First Failed Saving Throw:</i></b> The target is also weakened (save ends).<br/><b><i>Second Failed Saving Throw:</i></b> The target falls unconscious until the end of the encounter.<br/></p><p>Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 51.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n 