INSERT INTO `Trap` VALUES ('901','Rockslide','None','Hazard','1','Dungeon Master\'s Kit','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRockslide\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=901\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Rockslide<br/><span class=\"thSubHead\">Terrain</span><br/><span class=\"thLevel\">Level 1 Hazard<span class=\"thXP\">XP 100</span></span></h1><p class=\"thStat\"><b>Detect</b> Nature or Dungeoneering DC 19</p><span class=\"thInit\"><b>Initiative</b> +3</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Attack</b>  <img src=\"images/symbol/x.gif\" /> <b></b><b>Recharge</b> <img src = \"images/symbol/4a.gif\" /> <img src = \"images/symbol/5a.gif\" /> <img src = \"images/symbol/6a.gif\" /></p><p class=\"tbod\"><i>Attack</i>: Close burst 3 (creatures in burst); +4 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 2d6 + 6 damage.</p><p class=\"tbod\"><i>Miss</i>: Half damage.</p><p class=\"tbod\"><i>Effect</i>: The area of the burst becomes difficult terrain.</p><p class=\"tbod\"><i>Special</i>: As soon as the attack fails to recharge, the rockslide is over and does not attack again.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Avoid</b>: A creature that takes damage from the rockslide can use an immediate interrupt to take advantage of natural openings in the slide to avoid damage 