INSERT INTO `Trap` VALUES ('919','Elemental Turret','None','Trap','7','The Shadowfell','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nElemental Turret\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=919\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Elemental Turret<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 7 Trap<span class=\"thXP\">XP 300</span></span></h1><p class=\"thStat\"><b>Detect</b> automatic</p><span class=\"thInit\"><b>Initiative</b> +9</span><p class=\"thStat\"><b>HP</b> 40</p><p class=\"thStat\"><b>AC</b> 21, <b>Fortitude</b> 19, <b>Reflex</b> 10, <b>Will</b> -</p><p class=\"thStat\"><b>Resist</b> 5 all <b>Immune</b> necrotic, poison, psychic, forced movement, all conditions, ongoing damage </p><h2 class=\"thHead\">Traits</h2><p class=\"th2\"><b>Randomized Direction</b> </p><p class=\"thStat\">At the start of the turret’s turn, roll a d4 to determine whether it is facing north, east, south, or west.</p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Elemental Blast</b> (fire)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Attack</i>: Close blast special (creatures in the blast); +10 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 2d8 + 6 fire damage.</p><p class=\"tbod\"><i>Special</i>: The blast’s area of effect is a 2-by-3-square area extending directly out from the side the turret is facing.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Predict</b>: Perception DC 23 (minor action). <i>Success:</i> The character knows which direction the turret will fire on its next turn.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disable</b>: Thievery DC 23 (standard action). <i>Success:</i> The trap no longer functions.<i> Failure (18 or less):</i> The trap uses elemental blast as a free action, including the  disabling creature as a target.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/338050000\" target=\"_new\">The Shadowfell</a>, page(s) 2-4.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
