INSERT INTO `Trap` VALUES ('944','Warped Magic','None','Trap','10','Madness at Gardmore Abbey','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWarped Magic\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=944\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Warped Magic<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 10 Trap<span class=\"thXP\">XP 500</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC 18</p><span class=\"thInit\"><b>Initiative</b> +5</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Attack</b> (varies)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Effect</i>: A random square of warped magic makes the following attack. The burst’s area becomes difficult terrain until the end of the trap’s next turn.</p><p class=\"tbod\"><i>Attack</i>: Close burst 2 (each creature in the burst); +13 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 2d6 + 6 damage of a type determined randomly. Roll 1d6: 1—acid, 2—cold, 3—fire, 4—force, 5—lightning, 6—thunder. In addition, the target is slowed (save ends).</p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z2a.gif\" /><b>Attack</b> (varies)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature enters a square of warped magic.</p><p class=\"tbod\"><i>Attack</i> (<i>Opportunity Action</i>): Melee 1 (triggering creature); +13 vs. Fortitude</p><p class=\"tbod\"><i>Hit</i>: 2d8 + 9 damage of a type determined randomly. Roll 1d6: 1—acid, 2—cold, 3—fire, 4—force, 5—lightning, 6—thunder. In addition, the target is slowed (save ends).</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disable</b>: Thievery or Arcana DC 26 <i>Success:</i> One square of warped magic disappears.<i> Failure (21 or less):</i> The trap is triggered, attacking the attempting creature.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/355930000\" target=\"_new\">Madness at Gardmore Abbey</a>, page(s) 3-9.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
