INSERT INTO `Trap` VALUES ('1072','Reanimation Doorway','None','Trap','Varies','Dungeon Magazine 201','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nReanimation Doorway\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1072\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Reanimation Doorway<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level Varies Trap<span class=\"thXP\">XP Varies</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC (hard)</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z3a.gif\" /><b>Effect</b>  <img src=\"images/symbol/x.gif\" /> <b>Daily</b></p><p class=\"tbod\"><i>Trigger</i>: The corpse of a creature of a level up to the trap’s level + 3 passes through the doorway.</p><p class=\"tbod\"><i>Effect</i>: (Immediate Reaction): Ranged 1 (the triggering corpse); the target animates as an undead creature hostile to all other creatures. This creature has half the original creature’s full normal hit points, is immune to necrotic damage and poison damage, and gains the undead keyword. It has all the other statistics of the original creature and can make basic attacks, but the only powers it can use are the original creature’s at-will attack powers. The target remains animated for 1d6 + 4 rounds or until it drops to 0 hit points.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disarm</b>: Arcana (trained only) or Thievery, both  DC (hard). <i>Success:</i> The character defaces the right runes to disarm the trap.<i> Failure (by 5 or more):</i> The character takes 8 + the trap’s level necrotic damage.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/201\" target=\"_new\">Dungeon Magazine 201</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
