INSERT INTO `Trap` VALUES ('1075','Mirroring Portal','None','Trap','11','Dungeon Magazine 201','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMirroring Portal\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1075\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Mirroring Portal<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 11 Trap<span class=\"thXP\">XP 600</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC 27</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><b>Effect</b> (illusion, lightning)  <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"tbod\"><i>Trigger</i>: A creature passes through the doorway without saying the password or carrying the appropriate symbol.</p><p class=\"tbod\"><i>Effect</i>: One minute after the triggering creature passes through the doorway, 1d4 illusory duplicates of the creature emerge from the doorway and unerringly follow the creature. Each duplicate has the same defenses and speed as the triggering creature, and one-quarter of its hit points. The duplicates move toward the triggering creature and can enter its space. If a duplicate enters the triggering creature’s space, the duplicate vanishes and the triggering creature takes 2d6 lightning damage and is dazed until the end of its next turn.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Avoid</b>: Arcana (trained only) DC 27. <i>Success:</i> The character understands the password or the symbol needed to bypass the trap.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disarm</b>: Arcana (trained only) or Thievery DC 27. <i>Success:</i> The character defaces the right runes to disarm the trap.<i> Failure (22 or lower):</i> The character triggers the trap.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/201\" target=\"_new\">Dungeon Magazine 201</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
