INSERT INTO `Trap` VALUES ('1077','Summoning Portal','None','Trap','Varies','Dungeon Magazine 201','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSummoning Portal\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1077\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Summoning Portal<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level Varies Trap<span class=\"thXP\">XP Varies</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC (hard)</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Traits</h2><p class=\"th2\"><b>Special</b> </p><p class=\"thStat\">The trap is of a level equal to the level of the monster it summons.</p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><b>Effect</b>  <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"tbod\"><i>Trigger</i>: The door’s beast-head knocker is used to knock on the door.</p><p class=\"tbod\"><i>Effect</i>: At the start of the second round after someone triggers the trap, it summons a standard monster of its level. The monster appears in the doorway and is hostile to all other creatures. If the door is closed, it flies back open as if the summoned monster had opened the door. The monster remains for 5 rounds, and then it vanishes.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disarm</b>: Thievery DC (hard).Two Successes: The character removes the knocker without sounding it. Failure (by 5 or more): The character triggers the trap.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/201\" target=\"_new\">Dungeon Magazine 201</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1078','Dread Portal','None','Trap','13','Dungeon Magazine 201','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDread Portal\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1078\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Dread Portal<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 13 Trap<span class=\"thXP\">XP 800</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC 29</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z2a.gif\" /><b>Attack</b> (necrotic)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature touches the door or the doorway with anything other than the proper key.</p><p class=\"tbod\"><i>Attack</i> (<i>Immediate Reaction</i>): Melee 1 (triggering creature); +16 vs. Fortitude</p><p class=\"tbod\"><i>Hit</i>: Ongoing 15 necrotic damage, and the target is weakened (save ends both).</p><p class=\"tbod\"><i>Miss</i>: Ongoing 10 necrotic damage (save ends).</p><p class=\"th2\"><b>Effect</b> </p><p class=\"tbod\"><i>Trigger</i>: The door in the doorway is broken or otherwise destroyed.</p><p class=\"tbod\"><i>Effect</i>: (No Action): An oblivion wraith (Open Grave, page 193) appears and attacks whoever destroyed the door.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disarm</b>: Arcana (trained only) or Thievery DC 29. <i>Success:</i> The character defaces the right runes to disarm the trap.<i> Failure (24 or lower):</i> The character triggers the trap.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/201\" target=\"_new\">Dungeon Magazine 201</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
