INSERT INTO `Trap` VALUES ('1095','Possessed Sails','None','Minion Trap','14','Dungeon Magazine 203','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nPossessed Sails\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1095\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Possessed Sails<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 14 Minion Trap<span class=\"thXP\">XP 250</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Religion DC 29</p><span class=\"thInit\"><b>Initiative</b> +12</span><p class=\"thStat\"><b>HP</b> 1; a missed attack never damages a minion.</p><p class=\"thStat\"><b>AC</b> 28, <b>Fortitude</b> 25, <b>Reflex</b> 26, <b>Will</b> —</p><p class=\"thStat\"><b>Immune</b> necrotic, poison, psychic, all conditions, ongoing damage </p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z2a.gif\" /><b>Entangling Canvas</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Attack</i>: Melee 1 (one creature); +17 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: The target is blinded and grabbed (save ends both). The sail can entangle one Large creature or up to two Medium or smaller creatures at a time.</p><h2 class=\"thHead\">Move Actions</h2><p class=\"th2\"><b>Swimming Canvas</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Effect</i>: The sail swims up to 6 squares, and creatures grabbed by it are pulled with it, staying in a space of the DM’s choice within 1 square of the sail. The sail’s movement provokes no opportunity attacks.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Destroy</b>: Athletics or Religion DC 22 (standard action). <i>Requirement:</i> The character performing the check must be adjacent to the trap.  <i>Success:</i> The trap is destroyed.<i> Failure (17 or lower):</i> The trap attacks the character as a free action.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/203\" target=\"_new\">Dungeon Magazine 203</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1096','Collector\'s Dancing Sword','None','Hazard','23','Dungeon Magazine 204','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCollector\'s Dancing Sword\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1096\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Collector\'s Dancing Sword<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 23 Hazard<span class=\"thXP\">XP 5100</span></span></h1><p class=\"thStat\"><b>Detect</b> automatic</p><span class=\"thInit\"><b>Initiative</b> +19</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Traits</h2><p class=\"th2\"><b>Opportunistic Blade</b> </p><p class=\"thStat\">The sword can make opportunity attacks.</p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/S2.gif\" /> <b>Attack</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Attack</i>: Melee 1 (one creature); +28 vs. AC</p><p class=\"tbod\"><i>Hit</i>: 3d8 + 18 damage.</p><h2 class=\"thHead\">Move Actions</h2><p class=\"th2\"><b>Move</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Effect</i>: The sword flies up to 6 squares. It can hover.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Delay</b>: Arcana DC 37 (minor action). <i>Requirement:</i> The character attempting the skill check must be within 5 squares of the sword. <i>Success:</i> The sword cannot attack until the end of its next turn. <i> Failure (32 or lower):</i> As a free action, the sword flies up to 6 squares and attacks the character.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Parry</b>: Athletics or Acrobatics DC 37 (minor action). <i>Requirement:</i> The character attempting the skill check must be adjacent to the sword.  <i>Success:</i> The character pushes the sword up to 3 squares.<i> Failure (32 or lower):</i> As a free action, the sword attacks the character.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/204\" target=\"_new\">Dungeon Magazine 204</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
