INSERT INTO `Trap` VALUES ('1098','Demonweb Glyph','None','Trap','2','Dungeon Magazine 204','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDemonweb Glyph\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1098\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Demonweb Glyph<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 2 Trap<span class=\"thXP\">XP 125</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception or Arcana DC 9</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Attack</b> (poison, zone)  <img src=\"images/symbol/x.gif\" /> <b>Daily</b></p><p class=\"tbod\"><i>Trigger</i>: A creature enters the warded area without saying the password.</p><p class=\"tbod\"><i>Attack</i> (<i>No Action</i>): Close burst 3 (creatures in burst); +5 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 2d6 + 3 poison damage, and the target is slowed (save ends).</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>First Failed Saving Throw</i>: The target is instead immobilized (save ends).</p><p class=\"tbod\"><i>Effect</i>: The glyph creates a zone that lasts until the end of the encounter. All squares in the zone are difficult terrain.</p><p class=\"tbod\"><i>Special</i>: Once the glyph is triggered, it disappears and cannot be triggered again.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=