INSERT INTO `Trap` VALUES ('1121','Gorgon Mud','None','Trap','8','Dungeon Magazine 211','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGorgon Mud\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1121\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Gorgon Mud<br/><span class=\"thSubHead\">Hazard</span><br/><span class=\"thLevel\">Level 8 Trap<span class=\"thXP\">XP 350</span></span></h1><p class=\"thStat\"><b>Detect</b> Arcana or Perception DC 14</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Traits</h2><p class=\"th2\"><b>Thick Mud</b> </p><p class=\"thStat\">The mud is difficult terrain.</p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z2a.gif\" /><b>Attack</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature that has neither earth walk nor immunity to petrification enters a square containing the mud.</p><p class=\"tbod\"><i>Attack</i> (<i>Opportunity Action</i>): Melee 0 (triggering creature); +11 vs. Fortitude</p><p class=\"tbod\"><i>Hit</i>: The target is immobilized (save ends).</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>First Failed Saving Throw</i>: The target is restrained instead of immobilized (save ends).</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>Second Failed Saving Throw</i>: The target is petrified instead of restrained (save ends).</p><p class=\"tbod\"><i>Miss</i>: The target is slowed (save ends).</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Resist</b>: Athletics DC 14 (move action). <i>Success:</i> The character or one adjacent creature gains a +2 bonus to its next saving throw against the mud’s effect.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Siphon</b>: Arcana DC 19 (standard action). <i>Success:</i> The character siphons elemental energy from the mud, rendering inert 1 square plus 1 square for every 5 points by which the check result exceeds the DC.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/211\" target=\"_new\">Dungeon Magazine 211</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1122','Haunted River','None','Elite Trap','1','Dungeon Magazine 211','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nHaunted River\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1122\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Haunted River<br/><span class=\"thSubHead\">Hazard</span><br/><span class=\"thLevel\">Level 1 Elite Trap<span class=\"thXP\">XP 200</span></span></h1><p class=\"thStat\"><b>Detect</b> automatic</p><span class=\"thInit\"><b>Initiative</b> +3</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Standard Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z3a.gif\" /><b>Attack</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Attack</i>: Ranged 20 (one creature); +4 vs. Will</p><p class=\"tbod\"><i>Hit</i>: 1d10 + 3 psychic damage, and the target is dominated until the end of the river’s next turn. The target’s standard action while dominated is determined by the DM’s choice of vision (see below).</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>Daughter\'s Vision</i>: The target moves its speed toward the river and falls prone. If it ends its turn prone in the river, it takes 1d8 + 3 damage.</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>Stepmother\'s Vision</i>: The target charges its nearest ally and uses bull rush against it. If the ally is pushed into the river, it falls prone and takes 1d8 + 3 damage.</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>Cousin\'s Vision</i>: The target charges and makes a melee basic attack against its nearest ally.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Exorcise</b>: Arcana DC 19 or Religion DC 14 (standard action). <i>Requirement:</i> The character must be adjacent to one of the three corpses. <i>Success:</i> The corpse’s restless spirit is laid to rest, preventing the river from choosing that spirit when it makes an attack. The haunted river is disabled once all three spirits have been laid to rest, and any wraiths still active in the area are destroyed.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/211\" target=\"_new\">Dungeon Magazine 211</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
