INSERT INTO `Trap` VALUES ('1134','Charming Door','None','Trap','5','Dungeon Magazine 213','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCharming Door\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1134\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Charming Door<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 5 Trap<span class=\"thXP\">XP 200</span></span></h1><p class=\"thStat\"><b>Detect</b> see Countermeasures</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>HP</b> 20</p><p class=\"thStat\"><b>AC</b> 5, <b>Fortitude</b> 10, <b>Reflex</b> —, <b>Will</b> —</p><p class=\"thStat\"><b>Immune</b> cold, necrotic, poison, psychic, radiant, ongoing damage, forced movement, all conditions </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Attack</b> (charm)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature moves within 5 squares of the cottage door</p><p class=\"tbod\"><i>Attack</i>: Close blast 5 (creatures in blast); +6 vs. Will</p><p class=\"tbod\"><i>Hit</i>: On its turn, the target moves its speed toward the cottage and attempts to enter. When the target enters the cottage, it sits at the table and eats the feast upon the table (save ends).</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Detect</b>: Arcana DC 15 <i>Success:</i> You detect the cottage door as the source of the entrancing hospitality effect.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Delay/Disable</b>: Arcana DC 17 (minor action). <i>Success:</i> When the cottage door is closed, the entrancing hospitality effect cannot be cast. Its effects persist.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Destroy</b>: Strength DC 16 or 20 hit points/damage to door (standard action). Success: The door breaks, and the entrancing hospitality effect can no longer be cast.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/213\" target=\"_new\">Dungeon Magazine 213</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1135','Foot Crusher','None','Trap','5','Dungeon Magazine 213','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFoot Crusher\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1135\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Foot Crusher<br/><span class=\"thSubHead\">Object</span><br/><span class=\"thLevel\">Level 5 Trap<span class=\"thXP\">XP 200</span></span></h1><p class=\"thStat\"><b>Detect</b> Perception DC 22</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/S2.gif\" /> <b>Slam</b>  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature enters the open door.</p><p class=\"tbod\"><i>Attack</i> (<i>Immediate Interrupt Action</i>): Melee 1 (triggering creature); +8 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 1d10 + 8 damage, and the target is restrained (save ends).</p><p class=\"tbod\">&nbsp;&nbsp;&nbsp;&nbsp;<i>Aftereffect</i>: The target is slowed (save ends).</p><p class=\"tbod\"><i>Effect</i>: The door closes, ending the creature’s movement, and the trap resets.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Bypass</b>: Thievery DC 15 (standard action). <i>Success:</i> An adjacent character can allow one creature to pass through the door without triggering the trap.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Disable</b>: Thievery DC 22 (standard action). <i>Success:</i> An adjacent character can disable the trap.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/213\" target=\"_new\">Dungeon Magazine 213</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1136','Krakenweed','None','Hazard','5','Dungeon Magazine 213','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nKrakenweed\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1136\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Krakenweed<br/><span class=\"thSubHead\">Terrain</span><br/><span class=\"thLevel\">Level 5 Hazard<span class=\"thXP\">XP 200</span></span></h1><p class=\"thStat\"><b>Detect</b> Nature DC 22</p><p class=\"thStat\"><b>HP</b> 15 per square</p><p class=\"thStat\"><b>AC</b> 16, <b>Fortitude</b> 15, <b>Reflex</b> 17, <b>Will</b> —</p><p class=\"thStat\"><b>Resist</b> 5 force, 5 thunder <b>Immune</b> poison, psychic, forced movement, all conditions, ongoing damage <b>Vulnerable</b> 5 fire</p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/S2.gif\" /> <b>Kraken\'s Grasp</b> (poison)  <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"tbod\"><i>Trigger</i>: A creature enters a square within 2 squares of the krakenweed.</p><p class=\"tbod\"><i>Attack</i> (<i>Opportunity Action</i>): Melee 2 (triggering creature); +8 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: The target is grabbed (escape DC 15) and pulled 2 squares. Until the grab ends, the target takes ongoing 5 poison damage.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/213\" target=\"_new\">Dungeon Magazine 213</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r