 for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
INSERT INTO `Trap` VALUES ('1150','Salt Karst Collapse','None','Hazard','21','Dungeon Magazine 215','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSalt Karst Collapse\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=trap&amp;id=1150\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"thHead\">Salt Karst Collapse<br/><span class=\"thSubHead\">Terrain</span><br/><span class=\"thLevel\">Level 21 Hazard<span class=\"thXP\">XP 3200</span></span></h1><p class=\"thStat\"><b>Detect</b> Dungeoneering or Perception DC 19</p><span class=\"thInit\"><b>Initiative</b> —</span><p class=\"thStat\"><b>Immune</b> attacks </p><h2 class=\"thHead\">Triggered Actions</h2><p class=\"th2\"><img src=\"images/symbol/Z1a.gif\" /><b>Attack</b>  <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"tbod\"><i>Trigger</i>: A creature enters the hazard’s space.</p><p class=\"tbod\"><i>Attack</i> (<i>Opportunity Action</i>): Close burst 3 centered on the hazard\'s space (creatures in burst); +24 vs. Reflex</p><p class=\"tbod\"><i>Hit</i>: 3d10 + 4 damage and the target falls prone, and it is restrained and unable to stand. Until the target escapes (see Countermeasures), it has no line of sight or line of effect to other creatures, and other creatures have no line of effect or line of sight to it. Each time the target ends its turn restrained in this way, it takes 10 damage.</p><p class=\"tbod\"><i>Effect</i>: The burst’s area becomes difficult terrain.</p><h2 class=\"thHead\">Countermeasures</h2><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Avoid</b>: Acrobatics, Dungeoneering or Stealth DC 26 (part of a move action to move through the hazard\'s space). <i>Success:</i> The character can move through the hazard’s space without triggering the attack.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Escape</b>: Athletics or Acrobatics DC 35 (standard action by restrained character). <i>Success:</i> The character escapes.</p><p class=\"thBody\"><img src=\"images/symbol/x.gif\" />  <b>Rescue</b>: Athletics DC 26 (standard action by a character who is not restrained). <i>Success:</i> The character enables a restrained character to escape.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/215\" target=\"_new\">Dungeon Magazine 215</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n','');
